import PySimpleGUI as psg

minus_button = b''
plus_button = b''

def BluePSGButton(text):
	rounded_blue_button = b''
	return psg.Button(text, font=('Tahoma', 12), image_data=rounded_blue_button, button_color=('black', psg.theme_background_color()), mouseover_colors=('#303030', psg.theme_background_color()), border_width=0)

def enforce_number(window,values,event,decimal_allowed=True,negative_allowed=False):
	if len(values[event]):
		if negative_allowed and values[event] == '-':
				return
		if values[event][-1] not in ('.0123456789'):
			value = values[event][:-1]
			window[event].update(value)
		elif values[event][-1] == '.' and ('.' in values[event][:-1] or decimal_allowed == False):
			value = values[event][:-1]
			window[event].update(value)

def enforce_max_min(window,values,event,max,min,default=0):
	value = values[event]
	if len(value) and value != '-':
		try:
			value = int(value)
			if value > max:
				window[event].update(max)
			elif value < min:
				window[event].update(min)
		except:
			window[event].update(default)